import React, { useState, useEffect } from 'react';
import './App.css';

const FLASK_SERVER_IP = 'http://localhost:5000';
const ESP32_IP = 'http://192.168.1.34';

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [statusMessage, setStatusMessage] = useState("");
  const [isHolding, setIsHolding] = useState(false);

  // Function to control the LED via Flask
  const sendRequest = async (endpoint) => {
    setIsLoading(true);
    setStatusMessage("");
    try {
      const response = await fetch(`${FLASK_SERVER_IP}/${endpoint}`, {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' }
      });
      if (response.ok) {
        setStatusMessage(`Successfully sent request to ${endpoint}`);
      } else {
        setStatusMessage(`Failed to send request to ${endpoint}`);
      }
    } catch (error) {
      console.error(`Error sending request to ${endpoint}:`, error);
      setStatusMessage(`Error: Could not send request to ${endpoint}.`);
    }
    setIsLoading(false);
  };

  // Handle the "Hold On" button
  const handleHoldOnMouseDown = () => {
    setIsHolding(true);
    sendRequest("hold_on?action=start");
  };

  // Handle mouse up event globally
  const handleMouseUp = () => {
    if (isHolding) {
      sendRequest("hold_on?action=stop");
      setIsHolding(false);
    }
  };

  // Add a global mouseup event listener
  useEffect(() => {
    document.addEventListener('mouseup', handleMouseUp);
    return () => {
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isHolding]);

  return (
    <div className="App">
      <h1>ESP32 Camera & LED Control</h1>

      <div style={{ marginBottom: '20px' }}>
        <button onClick={() => sendRequest("on")}>Turn LED On</button>
        <button onClick={() => sendRequest("off")}>Turn LED Off</button>
      </div>

      {/* Updated "Hold to Turn LED On" button */}
      <div style={{ marginBottom: '20px' }}>
        <button
          onMouseDown={handleHoldOnMouseDown}
        >
          Hold to Turn LED On
        </button>
      </div>

      {isLoading && <p>Loading...</p>}

      {statusMessage && <p>{statusMessage}</p>}

      <h2>Live Stream</h2>
      <img
        src={`${ESP32_IP}/stream`}
        alt="ESP32 Stream"
        style={{ marginTop: '20px', border: '2px solid #333', width: '640px', height: '480px' }}
      />
    </div>
  );
}

export default App;
